import React, { useState } from 'react'

export default function ScreenTime() {

    const [secs, setsecs] = useState(60)
    const [mins, setmins] = useState(60)
    useEffect(() => {
        var secsInterval = setInterval(secsTime,1000)
        var minsInterval = setInterval(minTime,60000)
        },[])

const startPomodoro = () => {

    const minTime = () => {
        console.log(mins)
        setmins(mins-1)
    }

    const secsTime = () => {
        console.log(secs)
        if(secs <= 0)
        {
            if(mins<=0)
            {
                clearInterval(minsInterval)
                clearInterval(secsInterval)
                console.log('Pomodoro Completed')
            }
            else{
                setsecs(60)
            }
        } 
        setsecs(secs--)
    }



}



    return (
        <div>
            <h3>Your screen time is: </h3>
            {/* <span id="screen-time">{mins+" : "+secs}</span> */}
            <span>{secs}</span>
            <br /><br />
            <button onClick={startPomodoro}>Start</button>
        </div>
    )
}
